var initialState = {
    sort: {
        by: 'name',
        value: 1
    }
}
export var sort = (state = initialState.sort, action) => {
   action.type = 'SORT'
    if (action.type === 'SORT') {
        var { by, value } = state;
        console.log(state)
        return {
            sort: {
                by: by,
                value: value
            }
        }

    }
    return state
}   

export default sort;
