var initialState = {
    status: false,
    sort: {
        by: 'name',
        value: 1
    }
}

export var myReducer = (state = initialState, action) => {
    if (action.type === 'TOGGLE_STATUS') {
        state.status = !state.status
        return state
    }
    if (action.type === 'SORT') {
        var { by, value } = action.sort;
        var status = state.status;
        return {
            status: status,
            sort: {
                by: by,
                value: value
            }
        }

    }

}
export default myReducer;
