import React from 'react';
import HeaderList from './HeaderList'
import InputList from './InputList'
import DetailForm from './DetailForm'
import { connect } from 'react-redux';
import { filtertable } from '../reducers/filtertable';

class ListForm extends React.Component {
  render() {
    var { tasks ,filtertable} = this.props;
    if(filtertable.name) {
      tasks = tasks.filter((task)=>{
        return task.name.toLowerCase().indexOf(filtertable.name.toLowerCase()) !== -1
      })
    }  
    if(filtertable.status) {
      console.log(filtertable.status)
      tasks = tasks.filter((task)=>{
        if(filtertable.status === -1){
          return task;
        }else{
          return task.status === filtertable.status ? true : false;
        }
      })
    }  
    var elmTasks = tasks.map((task, index) => {
      return <DetailForm
        keys={tasks.id}
        key={index}
        index={index}
        task={task}z
        onUpdate={this.props.onUpdate}
      />
    });
    return (
      <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
        <table className="table table-bordered table-hover">
          <HeaderList />
          <tbody>
            <InputList
            />
            {elmTasks}
          </tbody>  
        </table>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    tasks: state.tasks,
    filtertable : state.filtertable
  }
};



export default connect(mapStateToProps)(ListForm);
