var initialState = {
    sort: {
        by: 'name',
        value: 1
    }
}

export var sort = (state = initialState, action) => {
    if (action.type === 'SORT') {
        var { by, value } = action.sort;
        var status = state.status;
        return {
            status: status,
            sort: {
                by: by,
                value: value
            }
        }

    }

}   

export default sort;
