var initialState = {
    sort: {
        by: 'name',
        value: 1
    }
}
export var sort = (state = initialState.sort, action) => {
    if (action.type === 'SORT') {
        var { by, value } = state;
        return {
            sort: {
                by: by,
                value: value
            }
        }

    }

}   

export default sort;
